mapper.update:
UPDATE tbl_coupon_info
store_id = #{store_id}, `desc` = #{desc}, coupon_money = #{coupon_money}, min_order_money = #{min_order_money}, coupon_total = #{coupon_total}, coupon_limit = #{coupon_limit}, start_time = #{start_time}, end_time = #{end_time}
WHERE coupon_id=#{coupon_id}

mapper.select:

SELECT a.*, (select count(1) from tbl_user_coupon_info where coupon_id = a.coupon_id) has_coupon_num
FROM tbl_coupon_info a
WHERE a.coupon_id=#{coupon_id}
