mapper.select:
    select * from tbl_order_info

    	 status='4'  

			AND buyer_user_id = #{buyer_user_id}


			AND seller_user_id = #{seller_user_id}


    order by order_id desc

    select * from tbl_order_info  

    	 status not in ('4','5','6')  

			AND buyer_user_id = #{buyer_user_id}


			AND seller_user_id = #{seller_user_id}


    order by order_id desc

mapper.update:
UPDATE tbl_order_info
amount_money = #{amount_money}, buyer_user_id = #{buyer_user_id}, buyer_user_name = #{buyer_user_name}, seller_user_id = #{seller_user_id}, seller_user_name = #{seller_user_name}, currency_unit = #{currency_unit}, buyer_name = #{buyer_name}, buyer_addr_id = #{buyer_addr_id}, buyer_phone = #{buyer_phone}, buyer_mobile = #{buyer_mobile}, send_type = #{send_type} send_no = #{send_no}, send_time = #{send_time}, freight = #{freight}, invoice_need = #{invoice_need}, invoice_title = #{invoice_title} pay_type = #{pay_type}, buyer_pay_time = #{buyer_pay_time}, trad_time = #{trad_time}, trad_finish_time = #{trad_finish_time}, seller_deliver_time = #{seller_deliver_time} buyer_confirm_time = #{buyer_confirm_time}, buyer_del = #{buyer_del}, seller_del = #{seller_del}, buyer_del_time = #{buyer_del_time} seller_del_time = #{seller_del_time}, buyer_score = #{buyer_score}, seller_score = #{seller_score}, status = #{status}, UPDATE_TIME = date_format(current_time,'%Y%m%d%H%i%s')
WHERE order_id = #{order_id}

update.foreach:
UPDATE tbl_order_detail
merch_id = #{item.merch_id}, merch_name = #{item.merch_name}, amount = #{item.amount}, unit = #{item.unit}, price = #{item.price},
WHERE order_id = #{item.orderId}

mapper.update:
UPDATE tbl_order_info
buyer_del = #{buyer_del},buyer_del_time=date_format(current_time,'%Y%m%d%H%i%s'), seller_del = #{seller_del},seller_del_time=date_format(current_time,'%Y%m%d%H%i%s'), update_time = date_format(current_time,'%Y%m%d%H%i%s')
WHERE order_id = #{order_id}
